import streamlit as st
from PIL import Image
import base64
import io
import pandas as pd

st.set_page_config(page_title="ShinXpert", layout="centered")

st.sidebar.title("ShinXpert")
st.sidebar.markdown("Smart Shin Guard Dashboard")

if "started" not in st.session_state:
    st.session_state.started = False

if not st.session_state.started:
    st.title("üëü Welcome to ShinXpert")
    st.markdown("Track player performance in real-time.")
    if st.button("Start Dashboard"):
        st.session_state.started = True
    st.stop()

players_data = [{'number': 7, 'time': '45 min', 'injury': '3%', 'speed': '25 km/h', 'heart': '72 bpm'}, {'number': 10, 'time': '60 min', 'injury': '5%', 'speed': '27 km/h', 'heart': '80 bpm'}, {'number': 23, 'time': '30 min', 'injury': '1%', 'speed': '20 km/h', 'heart': '70 bpm'}]
bpm_data = [72, 74, 75, 78, 80, 77, 76, 74, 73, 72]

search_input = st.text_input("Enter player number", "")
player = players_data[0]

if search_input:
    try:
        num = int(search_input)
        match = [p for p in players_data if p["number"] == num]
        if match:
            player = match[0]
        else:
            st.warning("Player not found. Showing default.")
    except:
        st.warning("Invalid number")

st.markdown(f"### Player #{player['number']}")

st.markdown("""
<style>
.card {
  background-color: #ffffff;
  border-radius: 15px;
  padding: 25px;
  color: #000;
  box-shadow: 0 4px 10px rgba(0,0,0,0.05);
  font-size: 18px;
  text-align: center;
}
.card span {
  font-weight: bold;
  color: #222;
}
</style>
""", unsafe_allow_html=True)

html_block = """
<div class="card">
üïí <span>{time}</span> Time played  &nbsp;|&nbsp;
‚öïÔ∏è <span>{injury}</span> Injury risk  &nbsp;|&nbsp;
‚ö° <span>{speed}</span> Speed<br>
üíì <span>{heart}</span> Heart rate
</div>
""".format(
    time=player['time'],
    injury=player['injury'],
    speed=player['speed'],
    heart=player['heart']
)
st.markdown(html_block, unsafe_allow_html=True)

# Graphique BPM
st.subheader("üìà Heart Rate Evolution")
bpm_df = pd.DataFrame(bpm_data, columns=["BPM"])
bpm_df.index.name = "Minute"
st.line_chart(bpm_df)

# Heatmap
st.subheader("üìä Player Heatmap")
heatmap_base64 = """"""
image_data = base64.b64decode(heatmap_base64)
image = Image.open(io.BytesIO(image_data))
st.image(image, use_container_width=True)

st.markdown("---")
st.caption("Made with üíô by ShinXpert Team")